import { LitElement, html, css } from 'lit';

class base64viewerElement extends LitElement {
  static getMetaConfig() {
    const svgBase64 = '';
    // plugin contract information
    return {
      controlName: 'neo-base64viewer',
      fallbackDisableSubmit: false,
      description: '',
      iconUrl: `data:image/svg+xml;base64,${svgBase64}`,
      groupName: 'Visual',
      version: '1.0',
      properties: {
        base64Data: {
          type: 'string',
          title: 'File as Base64',
          description: 'Please convert the file into Base64 and enter the value here.'
        },
      },
      standardProperties: {
        fieldLabel: true,
        description: true,
      }
    };
  }

  static get styles() {
    return css`
      :host {
        display: block;
        width: 100%;
        height: 100%;
      }
    `;
  }
  
  constructor() {
    super();
    this.base64Data = '';
  }

  render() {
    if (!this.base64Data) {
      return html`
      `;
    }

    const dataUrl = `data:application/pdf;base64,${this.base64Data}`;

    return html`
      <iframe
        src="${dataUrl}"
        width="100%"
        height="100%"
        frameborder="0"
        allowfullscreen
      ></iframe>
    `;
  }
}

customElements.define('neo-base64viewer', base64viewerElement);
